import os, shutil

# Dead agents
agents = [
    'global-agents/caddy-assistant.md',
    'global-agents/create-worktree-subagent.md', 
    'global-agents/work-completion-summary.md',
    'global-agents/hello-world-agent.md',
    'global-agents/llm-ai-agents-and-eng-research.md',
    'global-agents/team/context-manager.md',
]
for f in agents:
    if os.path.exists(f): os.unlink(f); print(f'  deleted {f}')

# Guardrails dir
if os.path.isdir('global-agents/team/guardrails'):
    shutil.rmtree('global-agents/team/guardrails')
    print('  deleted global-agents/team/guardrails/')

# Dead commands
cmds = ['start','sentient','all_tools','git_status','convert_paths_absolute','t_metaprompt_workflow','update_status_line']
for c in cmds:
    f = f'global-commands/{c}.md'
    if os.path.exists(f): os.unlink(f); print(f'  deleted {f}')

# Bench dir
if os.path.isdir('global-commands/bench'):
    shutil.rmtree('global-commands/bench')
    print('  deleted global-commands/bench/')

# Dead skills
for s in ['project-scaffolder','tdd-workflow']:
    d = f'global-skills/{s}'
    if os.path.isdir(d): shutil.rmtree(d); print(f'  deleted {d}/')

# Status lines v1-v8
for v in range(1,9):
    f = f'global-status-lines/mastery/status_line_v{v}.py'
    if os.path.exists(f): os.unlink(f); print(f'  deleted {f}')

# ralph-harness
if os.path.exists('scripts/ralph-harness.sh'): os.unlink('scripts/ralph-harness.sh'); print('  deleted scripts/ralph-harness.sh')

# coverage-html
if os.path.isdir('docs/coverage-html'):
    shutil.rmtree('docs/coverage-html')
    print('  deleted docs/coverage-html/')

# run_tests stub
if os.path.exists('global-hooks/framework/validators/run_tests.py'):
    os.unlink('global-hooks/framework/validators/run_tests.py')
    print('  deleted run_tests.py stub')

print('DONE')
"
